// Load PnL data for all recorders
async function loadRecorderPnL() {
    try {
        const pnlCells = document.querySelectorAll('.pnl-col[data-recorder-id]');
        for (const cell of pnlCells) {
            const recorderId = cell.dataset.recorderId;
            const response = await fetch(`/api/recorders/${recorderId}/pnl`);
            if (response.ok) {
                const data = await response.json();
                if (data.success) {
                    const pnl = data.total_pnl || 0;
                    const pnlEl = cell.querySelector('.pnl-value');
                    pnlEl.textContent = `$${pnl.toFixed(2)}`;
                    pnlEl.className = 'pnl-value ' + (pnl >= 0 ? 'positive' : 'negative');
                }
            }
        }
    } catch (e) {
        console.debug('Error loading PnL:', e);
    }
}

